function main(){window.clearInterval(blinktask),backspace=window.setInterval(function(){startBackspace()},TYPESPEED)}function startBackspace(){title=title.substr(0,title.length-1),document.getElementById("main-title").innerHTML=title+"|",0===title.length&&removeBackspace()}function removeBackspace(){window.clearInterval(backspace),window.setTimeout(function(){forwards=window.setInterval(function(){startTyping()},TYPESPEED)},MIDTIMEOUT)}function startTyping(){title=new_title.substr(0,types++),document.getElementById("main-title").innerHTML=title+"|",title===new_title&&endTyping()}function endTyping(){window.clearInterval(forwards),document.getElementById("main-title").innerHTML=title}function blink(){document.getElementById("main-title").innerHTML=blinked?title+"|":title,blinked=!blinked}var BLINKSPEED=300,TYPESPEED=200,STARTTIMEOUT=3e3,MIDTIMEOUT=900,title=document.getElementById("main-title").innerHTML,new_title="David Hacker",backspace,forwards,blinked=!1,blinktask=window.setInterval(function(){blink()},BLINKSPEED);window.setTimeout(function(){main()},STARTTIMEOUT);var types=1;